mapper.update:
UPDATE sucursal SET idEmpresa = #{idEmpresa}, nombreSucursal = #{nombreSucursal}, direccion = #{direccion}, codigoPosta = #{codigoPostal},
                    colonia = #{colonia}, ciudad = #{ciudad}, telefono = #{telefono}, latitud = #{latitud}, longitud = #{longitud}, nombreEncargado = #{nombreEncargado}
WHERE idSucursal = #{idSucursal}
